mapper.select:
 

 SELECT  gdsNum, gdsName, cateCode, cateCodeRef,cateName,
    gdsPrice, gdsStock, gdsDes, gdsDate,gdsImg,gdsThumbImg  
 from(select   /*+INDEX_DESC(tbl_goods pk_goods)*/ rownum rn,
    g.gdsNum, g.gdsName, g.cateCode, c.cateCodeRef, c.cateName,
    gdsPrice, gdsStock, gdsDes, gdsDate, g.gdsImg, g.gdsThumbImg
        from tbl_goods g
            inner join goods_category c
                on g.cateCode = c.cateCode           
            where g.cateCode = #{cateCode}
            or c.cateCodeRef = #{cateCodeRef} 
            and 
            
            
              
            rownum <=#{pageNum}*#{amount})   
        where rn >(#{pageNum}-1)*#{amount}

select g.gdsNum,g.gdsName, g.cateCode, c.cateCodeRef, c.cateName,g.gdsimg,g.gdsthumbimg,g.gdsDes,g.gdsPrice,g.gdsStock,g.like_cnt
from tbl_goods g inner join goods_category c on g.cateCode = c.cateCode
where g.gdsNum = #{gdsNum}

mapper.delete:
delete tbl_reply
    where repNum = #{repNum}
        and userId = #{userId}

mapper.select:
select userId
 from tbl_reply
     where repNum = #{repNum}

mapper.update:
update tbl_reply
    set
        repCon = #{repCon}
    where repNum = #{repNum}
        and userId = #{userId}

mapper.delete:
delete tbl_cart
    where cartNum = #{cartNum}
        and userId = #{userId}

mapper.select:
select mb
from ARK_newmember2
WHERE id = #{userId};

mapper.update:
UPDATE liketo
SET like_check = like_check + 1 
WHERE mno=#{userId} AND boardno=#{gdsNum}

UPDATE liketo
SET like_check = 0
WHERE mno=#{userId} AND boardno=#{gdsNum}

update tbl_goods

set LIKE_CNT = LIKE_CNT +1

   WHERE gdsNum= #{boardno}

update tbl_goods

set LIKE_CNT = LIKE_CNT -1

   WHERE gdsNum= #{boardno}

mapper.select:
SELECT COUNT(likeno) as cnt
FROM liketo
WHERE boardno=#{gdsNum} AND mno=#{userId}
