mapper.select:
select gdsNum , gdsName ,cateCode , gdsPrice , gdsStock , gdsDes , gdsImg , gdsDate ,gdsThumbImg ,cateCodeRef,cateName from(select /*+INDEX_DESC(tbl_goods pk_goods)*/ rownum rn, g.gdsNum , g.gdsName , g.cateCode , gdsPrice , gdsStock , gdsDes , gdsImg , gdsDate , g.gdsThumbImg , c.cateCodeRef , c.cateName
from tbl_goods g inner join goods_category c on g.cateCode = c.cateCode where
rownum <=#{pageNum}*#{amount}) where rn > (#{pageNum}-1)*#{amount}

mapper.update:
update tbl_goods set gdsName = #{gdsName}, cateCode = #{cateCode}, gdsPrice = #{gdsPrice}, gdsStock = #{gdsStock}, gdsDes = #{gdsDes},
gdsImg = #{gdsImg}, gdsThumbImg = #{gdsThumbImg}
where gdsNum = ${gdsNum}

update tbl_order
    set delivery = #{delivery}
where orderId = #{orderId}

update tbl_goods
    set gdsStock = gdsStock - #{gdsStock}
where gdsNum = #{gdsNum}

update tbl_goods
    set gdsStock = gdsStock + #{gdsStock}
where gdsNum = #{gdsNum}

update tbl_member_auth
     set auth = #{auth} 
 where id = #{userid}
